import { Component } from "react";

// import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [],
    };
    // console.log("constructor");
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((user) =>
        this.setState(() => {
          // console.log(user);
          return { monsters: user };
        })
      );
    // console.log("componentDidMount");
  }

  render() {
    console.log(this.state);
    // console.log("render");
    return (
      <div className="App">
        <input
          className="search-box"
          type="search"
          placeholder="search monsters"
          onChange={(e) => {
            let defaultMonsters = this.state.monsters;
            const array = this.state.monsters.filter((monster) =>
              monster.name.toLowerCase().includes(e.target.value.toLowerCase())
            );
            this.setState(() => {
              if (e.target.value === "") return { monster: defaultMonsters };
              return { monsters: array };
            });
            console.log(array);
            console.log(e.target.value === "");
          }}
        />
        {this.state.monsters.map((monster) => {
          return <h1 key={monster.id}>{monster.name}</h1>;
        })}
      </div>
    );
  }
}

export default App;
